import React,{useState} from 'react';

  let initialTextt = "type something";

  const App=()=>{
  const[initialText,setText]=useState(initialTextt);
  const clearData=()=>{
      setText("");
  }
  const handleData=(e)=>{
    setText(e.target.value)
  }

    return(
  <>
  <div className="container my-5">
    <textarea name="" id="" cols="30" rows="10" className="w-100" onChange={handleData} value={initialText}>{initialText}</textarea>
    <button className="btn btn-dark" onClick={clearData}>clear</button>
    <button className="btn btn-success">Preview</button>
  </div>
  </>
    )
  };
    

export default App;
