import React,{useState} from 'react';
const bgColorbg = "orange";
const texttC = "black";
const textt = "please work pal";
const App = () => {
  const [bgColor,newColor] = useState(bgColorbg);
  const [text,newText] = useState(textt);
  const [textC,newTextC] = useState(texttC);
  const cColor=()=>{
    newColor("green");
    newText("here you go");
    newTextC("red");
  }

  return(
    <>
    <div style={{backgroundColor:bgColor}}>
      <h1 style={{textcolor:textC}}>{text}</h1>
  
    </div>
    <button className="btn btn-dark" onClick={cColor}>click me</button>
    </>
  )
    
};

export default App;
