const App = () => {
  const[numbers,setNumbers]=useState(0);
  const increment=()=>{
setNumbers(increment+1);
  }
    return(
<>
<div className="container">
  <div className="col-lg-3 text-center my-4">
    <h1 className="">{numbers}</h1>
    <button className="btn btn-success"  onClick={increment}>incrementment</button>
  </div>
</div>
</>
    )
    
};

export default App;
